import React from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

class Create extends React.Component {
  constructor(props) {
    super(props);
    this.state = {name: '', role_id:''};
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(event) {
	  const state = this.state
	  state[event.target.name] = event.target.value
	  this.setState(state);
  }

  handleSubmit(event) {
    event.preventDefault();

    const config = { headers: { 'Content-Type': 'multipart/form-data' } };

    let data = new FormData();
    data.set('name', this.state.name);
    data.set('role_id', this.state.role_id);

    axios.post('/user',data,config).then(res => {
      console.log(res);
      if(res.status === 200) {
          alert("Website create successfully.");
      }
    })
  }
  render() {
    return (
		<div id="container">
		  <Link to="/">Websites</Link>
			  <p/>
			  <form onSubmit={this.handleSubmit}>
				<p>
					<label>Name:</label>
					<input type="text" name="name" value={this.state.title} onChange={this.handleChange} placeholder="Title" />
				</p>
				<p>
					<label>URL:</label>
					<input type="text" name="url" value={this.state.url} onChange={this.handleChange} placeholder="URL" />
				</p>
				<p>
					<input type="submit" value="Submit" />
				</p>
			  </form>
		   </div>
    );
  }
}

export default Create;
