import React from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Select from 'react-select';


class Create extends React.Component {
  constructor(props) {
    super(props);
    this.state = {name: '', role_id:''};
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.roles = [];
  }

  componentDidMount() {
    fetch('/roles')
        .then(response => {
            return response.json();
        }).then(result => {
            
            var rs = result.result;

            for(var i=0;i<rs.length;i++){
                var r = rs[i];
                var role = {label : r.Name, value : r.Id};
                this.roles.push(role);
            }
        });
}


  handleChange(event) {
	  const state = this.state
	  state[event.target.name] = event.target.value
	  this.setState(state);
  }

  handleSubmit(event) {
    event.preventDefault();

    const config = { headers: { 'Content-Type': 'multipart/form-data' } };

    let data = new FormData();
    data.set('name', this.state.name);
    data.set('role_id', this.state.role_id);

    axios.post('/user',data,config).then(res => {
      console.log(res);
      if(res.status === 200) {
          alert("Website create successfully.");
      }
    })
  }
  render() {

    const techCompanies = [
        { label: "Apple", value: 1 },
        { label: "Facebook", value: 2 },
        { label: "Netflix", value: 3 },
        { label: "Tesla", value: 4 },
        { label: "Amazon", value: 5 },
        { label: "Alphabet", value: 6 },
      ];
    return (
		<div id="container">
		  <Link to="/">Websites</Link>
			  <p/>
			  <form onSubmit={this.handleSubmit}>
				<p>
					<label>Name:</label>
					<input type="text" name="name" value={this.state.name} onChange={this.handleChange} placeholder="Name" />
				</p>
				<p>
					<label>Role:</label>
					<input type="text" name="role_id" value={this.state.role_id} onChange={this.handleChange} placeholder="Role" />
				</p>

                <p>
					<label>Role:</label>
					<Select options={ techCompanies }  defaultValue={{ label: "Amazon", value: 6 }}/>
				</p>


				<p>
					<input type="submit" value="Submit" />
				</p>
			  </form>
		   </div>
    );
  }
}

export default Create;
