import React from 'react';
import { Link, withRouter } from 'react-router-dom';
import axios from 'axios';

class Update extends React.Component {
  constructor(props) {
    super(props);
    this.state = {id: '', name: '', role_id: ''};
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  
  componentDidMount() {
	fetch('/user/' + this.props.match.params.id)
		.then(response => {
			return response.json();
		}).then(result => {
			console.log(result);
			this.setState({
				id:result.result.Id,
				name:result.result.Name,
				role_id:result.result.Role_Id
			});
		});
  }

  handleChange(event) {
      console.log(event.target.name);
      console.log(event.target.value);
	  const state = this.state;
      state[event.target.name] = event.target.value;
      //console.log("handleChange = " + JSON.stringify(this.state));
	  this.setState(state);
  }

  handleSubmit(event) {
      event.preventDefault();
      //console.log("handleSubmit = " + JSON.stringify(this.state));

      axios.post('localhost:3001/userupdate',{id: '', name: '', role_id: ''}).then(res => {
        
      })
     
      /*
	  fetch('/userupdate', {
        method: 'POST',
        headers: {
          'Content-type': 'application/x-www-form-urlencoded',
        },
        body: JSON.stringify({id: 1, name: '', role_id: ''}),
      }).then(response => {
				if(response.status === 200) {
					alert("Website update successfully.");
				}
            });

            */
            
          
  }
  
  render() {
    return (
			<div id="container">
			  <Link to="/">Websites</Link>
				  <p/>
				  <form onSubmit={this.handleSubmit}>
					<input type="hidden" name="id" value={this.state.id}/>
						<p>
						<label>Name:</label>
							<input type="text" name="name" value={this.state.name} onChange={this.handleChange} placeholder="Name" />
						</p>
						<p>
							<label>Role:</label>
							<input type="text" name="role_id" value={this.state.role_id} onChange={this.handleChange} placeholder="ROLE" />
						</p>
						<p>
							<input type="submit" value="Submit" />
						</p>
				  </form>
			   </div>
    );
  }
}

export default Update;
